function scrollToSection(event) {
     event.preventDefault();
     var $section = $($(this).attr("data-scroll"));
     let gap = document.querySelector(".header").clientHeight;
     let tick = false;
     let top_pos = $section.offset().top;
     console.log(gap,$section.offset().top)
 
     
     if (document.documentElement.scrollTop < 1) {
       tick = true;
     }
     $("html, body").animate(
       {
         scrollTop: top_pos - gap,
       },
       500,
       function () {
         setTimeout(() => {
           console.log(document.querySelector(".header").clientHeight);
           if (tick) {
             gap = document.querySelector(".header").clientHeight;
             $("html, body").animate(
               {
                 scrollTop: top_pos-(2*gap),
               },
               0
             );
           }
         }, 300);
       }
     );
   }


   $("[data-scroll]").on("click", scrollToSection);

   

  
