       //sticky section text animation
       let sticky_container = gsap.utils.toArray('[data-sticky="double"]');

       sticky_container.forEach((container, i) => {
         let texts = container.querySelectorAll('[data-left_sticky]'),
           imgs = container.querySelectorAll('[data-right_sticky]');

         let dft_color = $(container).attr("data-default_color");
         prev_sl = 0,
         //custom class add and set prop.
         gsap.set(container, {
           backgroundColor: dft_color,
         })
         gsap.set([$(texts).parent(), $(imgs).parent()], {
           height: window.innerHeight,
           position: "relative",
           display: "block",
         });
         for (let j = 0; j < texts.length; j++) {
           texts[j].classList.add("customClass" + (j + 1));
           gsap.set(texts[j], { height: window.innerHeight });
           if (j > 0) {
             gsap.set(texts[j], { y: window.innerHeight / 2, opacity: 0, });
             gsap.set(imgs[j], { opacity: 0, });
           }
         }

         setTimeout(() => {
           //animate section
           texts.forEach((text, i) => {
             //   console.log(document.querySelector(".main-head").clientHeight);
             let tl = gsap.timeline({
               scrollTrigger: {
                 trigger: container,
                 start: () => "top -" + (window.innerHeight * i),
                 end: () => "+=" + window.innerHeight,
                 scrub: true,
                 // markers: true,
                 toggleActions: "play none reverse none",
                 onUpdate: (self) => {
                   if (self.progress > 0) {
                     $(texts).removeClass("active");
                     $(text).addClass("active");

                     $(imgs).removeClass("active");
                     $(imgs[i]).addClass("active");
                  
                     gsap.set(imgs, {
                       opacity: 0,
                     });
                     gsap.set(imgs[i], {
                       opacity: 1,
                     });

                     gsap.set(container, {
                       backgroundColor: $(text).attr("data-bg_color"),
                     });
                     gsap.set(container, {
                       color: $(text).attr("data-color"),
                     });
                   }
                   else {
                     $(text).removeClass("active");
                     $(imgs).removeClass("active");
                   
                     gsap.set(container, {
                       backgroundColor: dft_color,
                       color: "inherit",
                     });
                   }
                 },
                 onEnter: () => {
                   if ($(text).attr("data-nav") != undefined) {
                     remove_wild_class("nav_");
                     $(".main-head").addClass("nav_" + $(text).attr("data-nav"));
                     imgs.play();

                     prev_sl = i;
                   }
               
                 },
                 onEnterBack: () => {
                   if ($(text).attr("data-nav") != undefined) {
                     remove_wild_class("nav_");
                     $(".main-head").addClass("nav_" + $(text).attr("data-nav"));
                     imgs.play();

                     prev_sl = i;
                   }
                  
                 },
                 onLeave: () => {
                   remove_wild_class("nav_");
                   imgs.load();
                   
                 },
                 onLeaveBack: () => {
                   remove_wild_class("nav_");
                   imgs.load();
                 
                 },
               }
             })
             if (i == 0) {
               tl.to(text, { opacity: 0, y: -window.innerHeight / 2 });
             }
             if (i > 0 && i < texts.length - 1) {
               tl.to(text, { opacity: 1, y: 0 })
                 .to(text, { opacity: 0, y: -window.innerHeight / 2 });
             }
             if (i == texts.length - 1) {
               tl.to(text, { opacity: 1, y: 0 });
             }
           });

           //pin section
           gsap.timeline({
             scrollTrigger: {
               trigger: container,
               scrub: 0.5,
               pin: true,
               start: () => "top top",
               end: () => "+=" + (texts.length * window.innerHeight),
             }
           });
         }, 50)
       });
     }, 100)
   }

 });
}

